package problems.dynamic;

/**
 * @author li
 * @version 1.0
 * @date 2020-04-21 01:17
 **/
public class ShortestPalindrome {

    public String shortestPalindrome(String s) {
        if (s == null || s.length() <= 1) {
            return s;
        }
        int r = prefixPalindromeEnd(s);
        String prefix = reverseString(s.substring(r));
        return prefix + s;
    }

    private boolean isPalindrome(String s) {
        if (s.length() <= 1) {
            return true;
        }
        char[] chars = s.toCharArray();
        int l = 0;
        int r = chars.length - 1;
        while (l < r) {
            if (chars[l] != chars[r]) {
                return false;
            }
            l++;
            r--;
        }
        return true;
    }

    /**
     * 从中间向两边扩展
     * 找出开头最长回文串的末端索引+1
     */
    private int prefixPalindromeEnd(String s) {
        int len = s.length();
        int r = 1;
        for (int i = 0; i < len; i++) {
            int lo = i;
            int hi = i;
            while (hi < s.length() - 1 && s.charAt(hi) == s.charAt(hi + 1)) {
                hi++;
            }
            i = hi;
            while (lo > 0 && hi < s.length() - 1 && s.charAt(lo - 1) == s.charAt(hi + 1)) {
                lo--;
                hi++;
            }
            if (lo == 0 && hi + 1 > r) {
                r = hi + 1;
            }
        }
        return r;
    }

    private String reverseString(String s) {
        if (s.length() <= 1) {
            return s;
        }
        char[] chars = s.toCharArray();
        int l = 0;
        int r = chars.length - 1;
        while (l < r) {
            char temp = chars[l];
            chars[l] = chars[r];
            chars[r] = temp;
            l++;
            r--;
        }
        return String.valueOf(chars);
    }


    public static void main(String[] args) {
        String s = "abcd";
        s = "";
        System.out.println(new ShortestPalindrome().shortestPalindrome(s));

    }

}
